'''
Да се направи графично приложение за игра на бесеница.
'''

import tkinter as tk
import random
from PIL import Image, ImageTk
from tkinter import messagebox
import base64
import io

words = ["hello", "world", "python", "tkinter", "hangman", "programming", "openai", "development"]
# Todo: Random word generator

# Base64 encoded images
images_base64 = {
    6: "",
    5: "",
    4: "",
    3: "",
    2: "",
    1: "",
    0: ""
    }


# Convert base64 strings to PhotoImage objects
def base64_to_image(base64_string):
    image_data = base64.b64decode(base64_string)
    image = Image.open(io.BytesIO(image_data))
    return ImageTk.PhotoImage(image)


# Word generator
chosen_word = random.choice(words)
guessed_word = ["_"] * len(chosen_word)
attempts = 6  # Number of tries
wrong_guesses = []


def check_letter():
    global attempts
    letter = entry_letter.get().lower()
    entry_letter.delete(0, tk.END)

    if len(letter) != 1 or not letter.isalpha():
        messagebox.showerror("Invalid input", "Please enter a single alphabet letter.")
        return

    if letter in wrong_guesses or letter in guessed_word:
        messagebox.showwarning("Already guessed", "You have already guessed this letter.")
        return

    if letter in chosen_word:
        for i in range(len(chosen_word)):
            if chosen_word[i] == letter:
                guessed_word[i] = letter
    else:
        attempts -= 1
        wrong_guesses.append(letter)

    update_display()

    if "_" not in guessed_word:
        messagebox.showinfo("Congratulations!", "You have guessed the word!")
        reset_game()
    elif attempts == 0:
        messagebox.showinfo("Game Over", f"You have run out of attempts. The word was: {chosen_word}")
        reset_game()


def update_display():
    # Update image
    tk_image = base64_to_image(images_base64[attempts])
    label_img.config(image=tk_image)
    label_img.image = tk_image

    # Update word, attempts, guesses
    label_word.config(text=" ".join(guessed_word))
    label_attempts.config(text=f"Attempts left: {attempts}")
    label_wrong_guesses.config(text=f"Wrong guesses: {', '.join(wrong_guesses)}")


def reset_game():
    global chosen_word, guessed_word, attempts, wrong_guesses
    chosen_word = random.choice(words)
    guessed_word = ["_"] * len(chosen_word)
    attempts = 6
    wrong_guesses = []
    update_display()


# Main window
root = tk.Tk()
root.title("Hangman Game")

# Configure the grid to expand with the window
root.columnconfigure(0, weight=1)
root.columnconfigure(1, weight=1)
root.rowconfigure(0, weight=1)
root.rowconfigure(1, weight=1)
root.rowconfigure(2, weight=1)
root.rowconfigure(3, weight=1)
root.rowconfigure(4, weight=1)
root.rowconfigure(5, weight=1)

# Image
label_img = tk.Label(root)
label_img.grid(row=0, column=0, columnspan=2, padx=10, pady=10, sticky="nsew")

# Word current condition
label_word = tk.Label(root, text=" ".join(guessed_word), font=("Calibri", 26))
label_word.grid(row=1, column=0, columnspan=2, padx=10, pady=10, sticky="nsew")

# Input letter label and entry
tk.Label(root, text="Enter a letter:", font=("Calibri", 16)).grid(row=2, column=0, padx=10, pady=10, sticky="e")
entry_letter = tk.Entry(root, text=" ".join(guessed_word), font=("Calibri", 20))
entry_letter.grid(row=2, column=1, padx=10, pady=10, sticky="w")

# Button for letter check
btn_check = tk.Button(root, text="Check", command=check_letter, width=15, height=1,
                      bg='lightgray', fg='black', font=('Helvetica', 14, 'bold'),
                      activebackground='lightgray', activeforeground='black',
                      borderwidth=3, relief='raised', padx=10, pady=5)
btn_check.grid(row=3, column=0, columnspan=2, pady=10)

# Remaining try
label_attempts = tk.Label(root, text=f"Attempts left: {attempts}", font=("Calibri", 16))
label_attempts.grid(row=4, column=0, columnspan=2, pady=10, sticky="nsew")

# Wrong guesses
label_wrong_guesses = tk.Label(root, text="Wrong guesses: ", font=("Calibri", 16), fg='red')
label_wrong_guesses.grid(row=5, column=0, columnspan=2, pady=10, sticky="nsew")

# Main loop
update_display()
root.mainloop()
